import React from "react";
import { createRoot } from "react-dom/client";
import { Stage, Layer, Star, Text } from "react-konva";
import axios from "axios";

const redStar = {
  id: "1",
  x: localStorage.getItem("redStarX") ? Number(localStorage.getItem("redStarX")) : 50,
  y: 65,
  isDragging: false,
  color: "#ff0000",
};

const App = () => {
  const [stars, setStars] = React.useState([redStar]);

  const handleDragMove = (e) => {
    const id = e.target.id();
    const newX = e.target.x();

    setStars(
      stars.map((star) => {
        if (star.id === id) {
          return {
            ...star,
            x: newX,
          };
        }
        return star;
      })
    );

    // Atualiza a posição do quadrado vermelho no localStorage
    localStorage.setItem("redStarX", newX);

    axios
      .put("/api/redStar", {
        x: newX,
      })
      .catch((error) => {
        console.error("Erro ao atualizar a posição X do quadrado vermelho:", error);
      });
  };

  return (
    <Stage width={window.innerWidth} height={window.innerHeight}>
      <Layer>
        <Text text="Teste spike 1 - react e konvas, MySql PHP" />
        {stars.map((star) => (
          <Star
            key={star.id}
            id={star.id}
            x={star.x}
            y={star.y}
            numPoints={2}
            innerRadius={40}
            outerRadius={40}
            fill={star.color}
            opacity={0.8}
            draggable={star.id !== "2"}
            rotation={45}
            shadowColor="black"
            shadowBlur={10}
            shadowOpacity={0.2}
            onDragMove={handleDragMove}
            // onClick={star.id === "2" ? handleBlueStarClick : undefined}
            dragBoundFunc={(pos) => ({
              x: pos.x,
              y: star.y, // Mantém a posição original no eixo Y
            })}
          />
        ))}
      </Layer>
    </Stage>
  );
};

const container = document.getElementById("root");
const root = createRoot(container);
root.render(<App />);
