import React, { useState, useEffect } from "react";
import { Rect, Text } from "react-konva";

const EditableRect = ({ x, y, width, height, color, text, onTextChange, isActive, onActivate, onTextSubmit }) => {
  
    const [isEditing, setIsEditing] = useState(false);
    const [editedText, setEditedText] = useState(text || "");
  
    useEffect(() => {
      setEditedText(text || "");
    }, [text]);
  
    const handleClick = () => {
      setIsEditing(true);
      onActivate();
    };
  
    const handleBlur = () => {
      setIsEditing(false);
      onTextChange(editedText);
    };
  
    const handleChange = (e) => {
      setEditedText(e.target.value);
    };
  
    const handleSaveClick = () => {
      setIsEditing(false);
      onTextChange(editedText);
    };
  
    return (
      <>
        <Rect
          x={x}
          y={y}
          width={width}
          height={height}
          fill={isActive ? "yellow" : color}
          opacity={1}
          draggable={false}
          onClick={handleClick}
          onTap={handleClick}
          listening={true}
        />
        {isEditing && (
          <>
            <input
              type="text"
              value={editedText}
              onChange={handleChange}
              onBlur={handleBlur}
              autoFocus
              style={{
                position: "absolute",
                top: y + height / 2,
                left: x + width / 2,
                transform: "translate(-50%, -50%)",
                width: width,
                fontSize: 20,
              }}
            />
            <button
              style={{
                position: "absolute",
                top: y + height / 2 + 40,
                left: x + width / 2,
                transform: "translate(-50%, -50%)",
              }}
              onClick={handleSaveClick}
            >
              Save
            </button>
          </>
        )}
        {text && (
          <Text
            x={x + width / 2}
            y={y + height / 2}
            text={text}
            fontSize={20}
            fill="#000"
            align="center"
            verticalAlign="middle"
            width={width}
            height={height}
            listening={false}
          />
        )}
      </>
    );
};

export default EditableRect;
