import "./index.css";
import React, { useEffect, useState } from "react";
import { createRoot } from "react-dom/client";
import { Stage, Layer, Rect, Circle } from "react-konva";
import axios from "axios";

const showAlert = () => {
  alert("Seu progresso foi salvo!");
};

const initialRectX1 = {
  id: "1",
  x: 10,
  y: 54,
  isDragging: false,
  color: "#ff0000",
  width: 120,
};

const initialRectX2 = {
  id: "2",
  x: 10,
  y: 154,
  isDragging: false,
  color: "#00ff00",
  width: 120,
};

const initialRectX3 = {
  id: "3",
  x: 10,
  y: 354,
  isDragging: false,
  color: "#ffff00",
  width: 120,
};

const initialRectX4 = {
  id: "4",
  x: 10,
  y: 454,
  isDragging: false,
  color: "#ff00ff",
  width: 120,
};

const initialCircle = {
  id: "5",
  x: 10,
  y: 261,
  isDragging: false,
  color: "#0000ff",
  radius: 15,
};

const App = () => {
  const [rects, setRects] = useState([initialRectX1, initialRectX2, initialRectX3, initialRectX4]);
  const [circle, setCircle] = useState(initialCircle);

  const handleDragMove = (e) => {
    const id = e.target.id();
    const newX = e.target.x();

    const updatedRects = rects.map((rect) => {
      if (rect.id === id) {
        return {
          ...rect,
          x: newX,
        };
      }
      return rect;
    });

    setRects(updatedRects);
  };

  const handleCircleDragMove = (e) => {
    const newX = e.target.x();
    const newY = Math.min(330, Math.max(261, e.target.y()));

    setCircle({
      ...circle,
      x: newX,
      y: newY,
    });
  };

  const handleDragEnd = (e) => {
    const id = e.target.id();
    const newX = e.target.x();

    if (newX >= 0 && newX <= 140) {
      e.target.x(10);
    } else if (newX >= 141 && newX <= 280) {
      e.target.x(150);
    } else if (newX >= 281 && newX <= 420) {
      e.target.x(290);
    } else if (newX >= 421 && newX <= 540) {
      e.target.x(430);
    }

    const updatedRects = rects.map((rect) => {
      if (rect.id === id) {
        return {
          ...rect,
          x: e.target.x(),
        };
      }
      return rect;
    });

    setRects(updatedRects);
  };

  const handleSaveClick = () => {
    const dataToPost = {
      rects,
      circle,
    };

    // Realize o POST para cada tabela
    axios
      .post("http://localhost:8080/index.php?controller=journeyPhase&method=postAllItems", dataToPost)
      .then(() => {
        return axios.post("http://localhost:8080/index.php?controller=thought&method=postAllItems", dataToPost);
      })
      .then(() => {
        return axios.post("http://localhost:8080/index.php?controller=userAction&method=postAllItems", dataToPost);
      })
      .then(() => {
        return axios.post("http://localhost:8080/index.php?controller=contactPoint&method=postAllItems", dataToPost);
      })
      .then(() => {
        return axios.post("http://localhost:8080/index.php?controller=emotion&method=postAllItems", dataToPost);
      })
      .then(() => {
        console.log("Dados salvos com sucesso!");
        showAlert();
      })
      .catch((error) => {
        console.error("Erro ao salvar os dados:", error);
      });
  };

  // Realize a busca para cada tabela
  useEffect(() => {
    axios.get("http://localhost:8080/index.php?controller=journeyPhase&method=getAllItems")
      .then((response) => {
        // Atualize o estado com os dados recuperados
        // Para cada tabela, faÃ§a a mesma chamada GET e atualize o estado correspondente
      })
      .catch((error) => {
        console.error("Erro ao buscar os dados:", error);
      });
  }, []);

  return (
    <div>
      <div style={{ textAlign: "center", padding: "10px", background: "#ffffff", fontFamily: "sans-serif", fontSize: "30px" }}>
        Journey Map Spike - React and Konva test
      </div>
      <div style={{ textAlign: "center", padding: "10px", background: "#d3d3d3", fontFamily: "sans-serif", fontSize: "30px" }}>
        FasesX: {rects[0].x} | AÃ§ÃµesX: {rects[1].x} | EmoÃ§ÃµesX: {circle.x} | EmoÃ§ÃµesY: {circle.y} | PensamentosX: {rects[2].x} | PontosX: {rects[3].x}
      </div>
      <div style={{ background: "repeating-linear-gradient(0deg,#d3d3d3,#d3d3d3 100px,white 100px,white 200px)" }}>
        <Stage width={window.innerWidth} height={window.innerHeight}>
          <Layer>
            {rects.map((rect) => (
              <Rect
                key={rect.id}
                id={rect.id}
                x={rect.x}
                y={rect.y}
                width={rect.width}
                height={20}
                fill={rect.color}
                draggable
                onDragMove={handleDragMove}
                onDragEnd={handleDragEnd}
              />
            ))}
            <Circle
              x={circle.x}
              y={circle.y}
              radius={circle.radius}
              fill={circle.color}
              draggable
              onDragMove={handleCircleDragMove}
            />
          </Layer>
        </Stage>
      </div>
      <button style={{ padding: "10px", fontSize: "20px", display: "block", margin: "10px auto" }} onClick={handleSaveClick}>
        Salvar Progresso
      </button>
    </div>
  );
};

createRoot(document.getElementById("root")).render(<App />);
